// ProductCatalog.js
import React, { useState, useEffect } from 'react';
import { getProducts } from '../services/productService';
import Product from './Product';
import './ProductCatalog.css'; // Import the CSS file

const ProductCatalog = () => {
  const [products, setProducts] = useState([]);
  const [filteredProducts, setFilteredProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await getProducts();
        console.log(data);
        const productArray = Array.isArray(data.products) ? data.products : [];
        setProducts(productArray);
        setFilteredProducts(productArray);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (!products) {
      return;
    }

    const filtered = products.filter((product) =>
      product.title.toLowerCase().includes(searchTerm.toLowerCase())
    );

    const sorted = [...filtered].sort((a, b) => {
      if (sortOption === 'price') {
        return a.price - b.price;
      } else if (sortOption === 'name') {
        return a.title.localeCompare(b.title);
      } else if (sortOption === 'rating') {
        return b.rating - a.rating;
      } else {
        return 0;
      }
    });

    setFilteredProducts(sorted);
  }, [searchTerm, sortOption, products]);

  return (
    <div>
      {/* Original layout without centered heading */}
      <div className="container mx-auto p-4">
        <div className="mb-4">
          {/* Search bar */}
          <input
            type="text"
            placeholder="Search products..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="p-2 border"
          />
        </div>

        <div className="mb-4">
          {/* Dropdown menu for sorting */}
          <label htmlFor="sort" className="mr-2">Sort by: </label>
          <select
            id="sort"
            value={sortOption}
            onChange={(e) => setSortOption(e.target.value)}
            className="p-2 border"
          >
            <option value="">None</option>
            <option value="price">Price</option>
            <option value="name">Name</option>
            <option value="rating">Popularity</option>
          </select>
        </div>

        {/* Display products with two columns on medium-sized screens and larger */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {filteredProducts.map((product) => (
            <Product key={product.id} {...product} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductCatalog;
